mapper.select:
select t.activity_type,t.check_latitude,t.check_longitude,t.memberCount_max,t.taskActivity_location,t.taskApply_count,t.taskEnd_time,t.taskStart_time,t.task_id,t.task_name,t.task_status,t.team_id
from pigeon.task t, pigeon.task_member tm
where t.task_id = tm.task_id and tm.user_id = #{userId,jdbcType=INTEGER}

mapper.update:
update pigeon.task
set taskApply_count = #{taskApplyCount,jdbcType=INTEGER}
where task_id = #{taskId,jdbcType=INTEGER}

update pigeon.task_member
set useCheck_longitude = #{useCheckLongitude,jdbcType=DECIMAL}, userCheck_latitude = #{userCheckLatitude,jdbcType=DECIMAL}, userTask_status = #{userTaskStatus,jdbcType=VARCHAR}, userPigeonEgg_count = #{userPigeonEggCount,jdbcType=INTEGER}
where user_id = #{userId,jdbcType=INTEGER} and task_id = #{taskId,jdbcType=INTEGER}

mapper.select:
select userTask_status
from pigeon.task_member
where task_id = #{taskId,jdbcType=INTEGER} and user_id = #{userId,jdbcType=INTEGER}
