mapper.select:
select t.task_id, t.task_name,t.team_id, t.taskActivity_location, t.memberCount_max, t.taskStart_time, t.taskEnd_time,t.task_status, t.check_longitude, t.check_latitude, t.activity_type, t.taskApply_count, t.task_introduction
from pigeon.task t, pigeon.task_member tm
where t.task_id = tm.task_id and tm.user_id = #{userId,jdbcType=INTEGER}

mapper.update:
update pigeon.task
set taskApply_count = #{taskApplyCount,jdbcType=INTEGER}
where task_id = #{taskId,jdbcType=INTEGER}

update pigeon.task
set task_status = #{taskStatus,jdbcType=VARCHAR}
where task_id = #{taskId,jdbcType=INTEGER}

mapper.select:
select userTask_status
from pigeon.task_member
where task_id = #{taskId,jdbcType=INTEGER} and user_id = #{userId,jdbcType=INTEGER}

select task_id, task_name, team_id, taskActivity_location, memberCount_max, taskStart_time, taskEnd_time, task_status, check_longitude, check_latitude, activity_type, taskApply_count, task_introduction
from pigeon.task
where team_id = #{teamId}

select task_id, task_name, team_id, taskActivity_location, memberCount_max, taskStart_time, taskEnd_time, task_status, check_longitude, check_latitude, activity_type, taskApply_count, task_introduction
from pigeon.task
where team_id = #{teamId} and task_status = #{taskStatus,jdbcType=VARCHAR}

select task_id, task_name, team_id, taskActivity_location, memberCount_max, taskStart_time, taskEnd_time, task_status, check_longitude, check_latitude, activity_type, taskApply_count, task_introduction
from pigeon.task
where team_id = #{teamId} and task_status != #{taskStatus,jdbcType=VARCHAR}
