mapper.update:
UPDATE pigeon.user SET

    user_name =#{userName},


    user_tel =#{userTel},


    user_sex =#{userSex},


    user_introduction = #{userIntroduction}

WHERE user_id =#{userId}

update pigeon.user
set pigeonEgg_count = pigeonEgg_count + #{pigeonEggCount,jdbcType=INTEGER}
where user_id = #{userId,jdbcType=INTEGER}
